#include<iostream> #include<vector> #include<algorithm> using namespace std; int main(){ bool first; int64_t N, Q; cin >> N; vector<int> A(N), B(N), C(N); vector<int64_t> sum(N); for(int i = 0; i < N; i++){ cin >> A[i]; } cin >> Q; for(int i = 0; i < Q; i++){ cin >> B[i] >> C[i]; } sort(A.begin(), A.end()); for(int i = 0; i < Q; i++){ if(B[i] < A[Q / 2]){ for(int j = 0; j < Q / 2; j++){ if(A[j] == B[i]){ A[j] = C[i]; } sum[i] += A[j]; } } else{ for(int j = Q / 2 + 1; j < Q; j++){ if(A[j] == B[i]){ A[j] = C[i]; } sum[i] += A[j]; } } } for(int i = 0; i < Q; i++){ cout << sum[i] ; } }
